Scripts.OnLoad:
                GameTooltip_OnLoad(self);
				self.shoppingTooltips = { ShoppingTooltip1, ShoppingTooltip2 };
				if (BattlePetTooltip) then
                    BattlePetTooltip:Hide();
                end

Scripts.OnHide:
                GameTooltip_OnHide(self);
				ShoppingTooltip1:Hide();
                ShoppingTooltip2:Hide();
                if (BattlePetTooltip) then
                    BattlePetTooltip:Hide();
                end
